import React from "react";
import { Link } from "react-router-dom";
import { collegeata } from "../data/collegdata";

const MadhyaPradeshColleges = () => {
  return (
    <div style={{ padding: "20px" }}>
      {Object.keys(collegedata).map((stream) => (
        <section key={stream} style={{ marginBottom: "30px" }}>
          <h2>
            <Link to={`/colleges/${stream}`} style={{ textDecoration: "none" }}>
              {stream} Colleges
            </Link>
          </h2>
          <ul>
            {collegedata[stream].map((college, idx) => (
              <li key={idx}>
                {college.name} - {college.city}
              </li>
            ))}
          </ul>
        </section>
      ))}
    </div>
  );
};

export default MadhyaPradeshColleges;
