import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
// We no longer import the static data file

const CollegeListPage = () => {
  const { stream } = useParams();
  const navigate = useNavigate();

  // State to hold colleges fetched from the backend
  const [colleges, setColleges] = useState([]);
  // State to handle loading status
  const [isLoading, setIsLoading] = useState(true);
  // State to handle any errors during fetching
  const [error, setError] = useState(null);

  // useEffect to fetch data when the component mounts or the stream changes
  useEffect(() => {
    const fetchColleges = async () => {
      try {
        setIsLoading(true);
        // Fetch all colleges from your backend API
        const response = await fetch('http://localhost:5000/api/colleges');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const allColleges = await response.json();
        
        // Filter the colleges by the stream from the URL
        const filteredColleges = allColleges.filter(college => college.stream === stream);
        setColleges(filteredColleges);

      } catch (err) {
        setError(err.message);
        console.error("Failed to fetch colleges:", err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchColleges();
  }, [stream]); // The effect re-runs if the stream parameter changes

  const handleApplyClick = (collegeName) => {
    navigate('/enrollnow', { state: { collegeName: collegeName } });
  };

  const handleDetailsClick = (collegeName) => {
    const encodedCollegeName = encodeURIComponent(collegeName);
    navigate(`/colleges/${stream}/${encodedCollegeName}`);
  };

  const formatStreamTitle = (slug) => {
    if (!slug) return '';
    return slug.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  return (
    <div className="college-list-container">
      <h1 className="college-list-title">{formatStreamTitle(stream)} Colleges</h1>
      
      {isLoading ? (
        <p>Loading colleges...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : colleges.length === 0 ? (
        <p>No colleges found for this stream in the database.</p>
      ) : (
        <ul className="college-list-ul">
          {colleges.map((college) => (
            <li key={college._id} className="college-list-item"> {/* Use the database _id as the key */}
              <div className="college-info">
                <span className="college-name">{college.name}</span>
                <span className="college-city">{college.city}</span>
              </div>
              <div className="college-actions">
                <button
                  className="btn-details"
                  onClick={() => handleDetailsClick(college.name)}
                >
                  Get Details
                </button>
                <button
                  className="btn-apply"
                  onClick={() => handleApplyClick(college.name)}
                >
                  Apply Now
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CollegeListPage;
