import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // make sure you import like this

const AdminDashboard = ({ onLogout }) => {
  const [colleges, setColleges] = useState([]);
  const [formData, setFormData] = useState({
    name: '', city: '', stream: 'engineering', fees: '',
    description: '', courses: '', website: ''
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // ✅ Fetch colleges function
  const fetchColleges = useCallback(async () => {
    const token = localStorage.getItem('adminToken');
    if (!token) return;

    try {
      const response = await fetch('http://localhost:5000/api/colleges', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error("Failed to fetch colleges");
      const data = await response.json();
      if (Array.isArray(data)) setColleges(data);
    } catch (err) {
      console.error(err);
    }
  }, []);

  // ✅ Logout function
  const handleLogout = useCallback(() => {
    localStorage.removeItem('adminToken');
    if (onLogout) onLogout(false);
    navigate("/admin");
  }, [navigate, onLogout]);

  // ✅ Check token and auto-logout on expiry
  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (!token) {
      handleLogout();
      return;
    }

    try {
      const decoded = jwtDecode(token);
      if (decoded.exp * 1000 < Date.now()) {
        handleLogout();
      }
    } catch (err) {
      console.error('Invalid token', err);
      handleLogout();
    }
  }, [handleLogout]);

  // ✅ Fetch colleges on mount
  useEffect(() => {
    fetchColleges();
  }, [fetchColleges]);

  // --- Form handlers ---
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    const collegeData = {
      ...formData,
      courses: formData.courses
        ? formData.courses.split(',').map(course => course.trim())
        : []
    };

    try {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setMessage("Unauthorized. Please login again.");
        handleLogout();
        return;
      }

      const response = await fetch('http://localhost:5000/api/colleges', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(collegeData),
      });

      if (response.ok) {
        setMessage('✅ College added successfully!');
        setFormData({ name: '', city: '', stream: 'engineering', fees: '', description: '', courses: '', website: '' });
        fetchColleges();
      } else {
        const errData = await response.json();
        setMessage(`Error: ${errData.message || 'Failed to add college.'}`);
      }
    } catch (err) {
      setMessage('Error: Could not connect to the server.');
    }
  };

  // ✅ Delete college
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this college?")) return;

    try {
      const token = localStorage.getItem('adminToken');
      const response = await fetch(`http://localhost:5000/api/colleges/${id}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        setMessage("✅ College deleted successfully!");
        setColleges(prev => prev.filter(c => c._id !== id));
      } else {
        setMessage("Error deleting college.");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error deleting college.");
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
        <button onClick={handleLogout} className="btn-secondary">
          Logout
        </button>
      </div>

      <div className="admin-form-container">
        <h3>Add New College</h3>
        <form onSubmit={handleSubmit}>
          <input name="name" value={formData.name} onChange={handleChange} placeholder="College Name" required />
          <input name="city" value={formData.city} onChange={handleChange} placeholder="City" required />
          <select name="stream" value={formData.stream} onChange={handleChange}>
            <option value="engineering">Engineering</option>
            <option value="management">Management</option>
            <option value="law">Law</option>
            <option value="medical-allied-sciences">Medical</option>
            <option value="arts-science-commerce">Arts, Science & Commerce</option>
            <option value="agriculture">Agriculture</option>
            <option value="architecture-design">Architecture & Design</option>
          </select>
          <input name="fees" value={formData.fees} onChange={handleChange} placeholder="Fees (e.g., ₹1,50,000 per year)" />
          <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description"></textarea>
          <input name="courses" value={formData.courses} onChange={handleChange} placeholder="Courses (comma-separated)" />
          <input name="website" value={formData.website} onChange={handleChange} placeholder="Website URL" />
          <button type="submit" className="btn-primary">Add College</button>
        </form>
        {message && <p className="admin-message">{message}</p>}
      </div>

      <div className="admin-college-list">
        <h3>Existing Colleges ({colleges.length})</h3>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>City</th>
              <th>Stream</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {colleges.map(college => (
              <tr key={college._id}>
                <td>{college.name}</td>
                <td>{college.city}</td>
                <td>{college.stream}</td>
                <td>
                  <button onClick={() => handleDelete(college._id)} className="btn-danger">
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
