import React from "react";
import { useParams, Link } from "react-router-dom";
import { collegeData } from "..data/"; // Adjusted import path
// import './App.css'; // Adjusted to use a single main stylesheet

const CollegeDetailPage = () => {
  const { stream, collegeName } = useParams();
  const colleges = collegeData[stream] || [];
  
  // Find the college, ensuring names match by decoding the URL component
  const college = colleges.find(c => c.name === decodeURIComponent(collegeName));

  if (!college) {
    return (
      <div className="college-detail-container">
        <p>College not found.</p>
        <Link to="/" className="back-link">&larr; Back to Home</Link>
      </div>
    );
  }

  return (
    <div className="college-detail-container">
      <Link to={`/colleges/${stream}`} className="back-link">&larr; Back to {stream} Colleges</Link>
      <div className="college-detail-card">
        <h1 className="college-detail-title">{college.name}</h1>
        <p className="college-detail-city">{college.city}</p>
        
        <div className="detail-section">
          <h3>Description</h3>
          <p>{college.description || "No description available."}</p>
        </div>

        <div className="detail-section">
          <h3>Estimated Fees</h3>
          <p>{college.fees || "Not available."}</p>
        </div>

        <div className="detail-section">
          <h3>Courses Offered</h3>
          <p>{college.courses ? college.courses.join(", ") : "Not available."}</p>
        </div>
        
        {college.website && (
          <div className="detail-section">
            <h3>Website</h3>
            <a href={college.website} target="_blank" rel="noopener noreferrer" className="website-link">
              Visit Website
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default CollegeDetailPage;
