import React from 'react';

// --- Helper Data (Simulating fetched data from the website) ---
const collegesData = [
    { name: "SAGE University, Indore", image: "https://placehold.co/400x300/1/FFF?text=SAGE+University" },
    { name: "Symbiosis University", image: "https://placehold.co/400x300/2/FFF?text=Symbiosis+University" },
    { name: "Indore Institute of Science", image: "https://placehold.co/400x300/3/FFF?text=IIST" },
    { name: "Acropolis Group of Institutions", image: "https://placehold.co/400x300/4/FFF?text=Acropolis" },
    { name: "Medicaps University", image: "https://placehold.co/400x300/5/FFF?text=Medicaps" },
    { name: "Prestige Institute of Management And Research", image: "https://placehold.co/400x300/6/FFF?text=PIMR" },
    { name: "Renaissance College of Commerce & Management", image: "https://placehold.co/400x300/7/FFF?text=Renaissance+College" },
    { name: "Renaissance University", image: "https://placehold.co/400x300/8/FFF?text=Renaissance+University" },
];


// --- Sub-Components (Internal) ---

const CollegeCard = ({ name, image }) => (
    <div className="college-card">
        <img src={image} alt={name} className="college-card-img" />
        <div className="college-card-content">
            <h4>{name}</h4>
        </div>
    </div>
);


// --- Main Colleges Section Component ---
export default function CollegesSection() {
    return (
        <section className="section">
            <div className="container">
                <div className="section-header">
                    <h2>Our Popular Colleges</h2>
                    <p>Explore top-rated institutions for your future.</p>
                </div>
                <div className="grid-4">
                    {collegesData.map((college, index) => (
                        <CollegeCard key={index} name={college.name} image={college.image} />
                    ))}
                </div>
            </div>
        </section>
    );
}
