import React from 'react';
// ‚≠ê 1. Import the custom hook
import useOnScreen from './hooks/useOnScreen'; // Adjust the path as needed

// Let's create a reusable animated component to keep things clean
const AnimatedCard = ({ children, delay = 0 }) => {
  // ‚≠ê 2. Use the hook to get a ref and the visibility state
  const [ref, isVisible] = useOnScreen({
    threshold: 0.2, // Trigger animation when 20% of the card is visible
  });

  return (
    <div
      ref={ref}
      // ‚≠ê 3. Apply the CSS classes based on visibility
      className={`why-choose-us-card fade-in-section ${isVisible ? 'is-visible' : ''}`}
      style={{ transitionDelay: `${delay}ms` }} // Optional: for staggered effect
    >
      {children}
    </div>
  );
};


// Your main component
const WhyChooseUsSection = () => {
  const reasons = [
    { title: 'Expert Tutors', icon: 'üéì', description: 'Learn from the best in the industry.' },
    { title: 'Flexible Learning', icon: '‚è∞', description: 'Study at your own pace, anytime, anywhere.' },
    { title: 'Great Community', icon: '‚ù§Ô∏è', description: 'Connect with peers and mentors.' },
  ];

  return (
    <section className="section why-choose-us-section">
      <div className="container">
        <div className="section-header">
          <h2>Why Choose Us?</h2>
          <p>We provide the best tools and community for your education.</p>
        </div>
        <div className="grid-3">
          {reasons.map((reason, index) => (
            // ‚≠ê 4. Use the new AnimatedCard component
            <AnimatedCard key={index} delay={index * 100}> {/* Stagger the animation */}
              <div className="why-choose-us-icon">{reason.icon}</div>
              <h3>{reason.title}</h3>
              <p>{reason.description}</p>
            </AnimatedCard>
          ))}
        </div>
      </div>
    </section>
  );
};

export default WhyChooseUsSection;
