// export default MadhyaPradeshColleges;
import React from 'react';
import { collegeData } from '../data/collegedata.js'; // Assuming data is in the data folder

const MadhyaPradeshColleges = ({ stream }) => {
    // A helper function to format the stream title from a slug
    const formatStreamTitle = (slug) => {
        return slug
            .replace(/-/g, ' ')
            .replace(/([A-Z])/g, ' $1') // Handles potential camelCase
            .replace(/^./, (str) => str.toUpperCase());
    };

    const colleges = collegeData[stream] || [];
    const streamTitle = formatStreamTitle(stream);

    // Placeholder functions for button clicks
    const handleApplyClick = (collegeName) => {
        console.log(`"Apply Now" clicked for: ${collegeName}`);
        // You could navigate to an enrollment form here
    };

    const handleDetailsClick = (collegeName) => {
        console.log(`"Get Details" clicked for: ${collegeName}`);
        // You could navigate to a detailed page for the college here
    };

    return (
        <div className="mp-colleges-container">
            <a href="#/" className="back-link">&larr; Back to Categories</a>
            <section className="stream-section">
                <h2 className="stream-title">{streamTitle} Colleges</h2>
                {colleges.length > 0 ? (
                    <ul className="college-list">
                        {colleges.map((college, index) => (
                            <li key={index} className="college-item">
                                <div className="college-info">
                                    <span className="college-name">{college.name}</span>
                                    <span className="college-city">{college.city}</span>
                                </div>
                                <div className="college-actions">
                                    <button className="btn-details" onClick={() => handleDetailsClick(college.name)}>Get Details</button>
                                    <button className="btn-apply" onClick={() => handleApplyClick(college.name)}>Apply Now</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No colleges found for this stream. Please check back later.</p>
                )}
            </section>
        </div>
    );
};

export default MadhyaPradeshColleges;
