import React, { useState, useEffect } from 'react';

// --- SVG Icons ---
// Using simple but effective SVG icons for each category.
const EngineeringIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-circuit-board"><rect width="18" height="18" x="3" y="3" rx="2" /><path d="M11 9h4a2 2 0 0 0 2-2V3" /><path d="M11 15h4a2 2 0 0 1 2 2v4" /><path d="M5 15h2" /><path d="M5 9h2" /><circle cx="9" cy="9" r="1" /><circle cx="15" cy="15" r="1" /><circle cx="9" cy="15" r="1" /><circle cx="15" cy="9" r="1" /></svg>
);
const ManagementIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-area-chart"><path d="M3 3v18h18" /><path d="M7 12v5h12V8l-5 5-4-4Z" /></svg>
);
const MedicalIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-stethoscope"><path d="M4.8 2.3A.3.3 0 1 0 5 2a.3.3 0 0 0-.2.3" /><path d="M6.4 4c.2-.2.5-.2.7 0l4.3 4.3c.2.2.2.5 0 .7l-1.3 1.3c-.2.2-.5.2-.7 0l-4.3-4.3a.5.5 0 0 1 0-.7Z" /><path d="m12.5 12.5 4.4 4.4" /><path d="M11.1 11.1 7.2 7.2" /><path d="M14 2a2 2 0 0 1 2 2v2" /><path d="M21 9a2 2 0 0 1-2 2h-2" /><path d="M3 15a2 2 0 0 0-2 2v2" /><path d="M10 22a2 2 0 0 0 2-2h-2" /><path d="M17.8 17.8a5 5 0 1 1-7-7l-.7-.7" /></svg>
);
const LawIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-scale"><path d="m16 16 3-8 3 8c-.87.65-1.92 1-3 1s-2.13-.35-3-1Z" /><path d="m2 16 3-8 3 8c-.87.65-1.92 1-3 1s-2.13-.35-3-1Z" /><path d="M7 21h10" /><path d="M12 3v18" /><path d="M3 7h2c2 0 5-1 7-2 2 1 5 2 7 2h2" /></svg>
);
const ArtsIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-scroll-text"><path d="M8 21h12a2 2 0 0 0 2-2v-2H10v2a2 2 0 1 1-4 0V5a2 2 0 1 0-4 0v3h10v10a2 2 0 0 0 2 2Z" /><path d="M16 17h-1" /><path d="M16 13h-1" /><path d="M16 9h-1" /></svg>
);
const AgricultureIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-sprout"><path d="M7 20h10" /><path d="M12 20V4" /><path d="M12 4c0-2.21-1.79-4-4-4" /><path d="M12 4c0-2.21 1.79-4 4-4" /><path d="M12 10c0-2.21-1.79-4-4-4" /><path d="M12 10c0-2.21 1.79-4 4-4" /></svg>
);
const ArchitectureIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-drafting-compass"><circle cx="12" cy="12" r="10" /><path d="m12 18-3-6 9-3-6 9" /><circle cx="12" cy="12" r="2" /></svg>
);
const BackIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-arrow-left mr-2 h-5 w-5"><path d="m12 19-7-7 7-7"/><path d="M19 12H5"/></svg>
);


// --- Data ---
const categories = [
  { name: 'Engineering', count: '50+', Icon: EngineeringIcon },
  { name: 'Management', count: '60+', Icon: ManagementIcon },
  { name: 'Medical & Allied Sciences', count: '25+', Icon: MedicalIcon },
  { name: 'Law', count: '20+', Icon: LawIcon },
  { name: 'Arts, Science & Commerce', count: '100+', Icon: ArtsIcon },
  { name: 'Agriculture', count: '20+', Icon: AgricultureIcon },
  { name: 'Architecture & Design', count: '20+', Icon: ArchitectureIcon },
];

const collegeData = {
    "Management": [
        { "name": "Indian Institute of Management (IIM)", "city": "Indore" },
        { "name": "Atal Bihari Vajpayee Indian Institute of Information Technology and Management (ABV-IIITM)", "city": "Gwalior" },
        { "name": "Prestige Institute of Management and Research", "city": "Indore" },
    ],
    "Engineering": [
        { "name": "Indian Institute of Technology (IIT)", "city": "Indore" },
        { "name": "Maulana Azad National Institute of Technology (MANIT)", "city": "Bhopal" },
        { "name": "Shri Govindram Seksaria Institute of Technology and Science (SGSITS)", "city": "Indore" },
    ],
    "Law": [
        { "name": "National Law Institute University (NLIU)", "city": "Bhopal" },
        { "name": "Dharmashastra National Law University (DNLU)", "city": "Jabalpur" },
        { "name": "Indore Institute of Law", "city": "Indore" },
    ],
    "Medical & Allied Sciences": [
        { "name": "All India Institute of Medical Sciences (AIIMS)", "city": "Bhopal" },
        { "name": "Mahatma Gandhi Memorial Medical College (MGM)", "city": "Indore" },
        { "name": "Gandhi Medical College", "city": "Bhopal" },
    ],
    "Arts, Science & Commerce": [
        { "name": "Holkar Science College", "city": "Indore" },
        { "name": "St. Aloysius College", "city": "Jabalpur" },
        { "name": "The Bhopal School of Social Sciences (BSSS)", "city": "Bhopal" },
    ],
    "Agriculture": [
        { "name": "Jawaharlal Nehru Krishi Vishwavidyalaya (JNKVV)", "city": "Jabalpur" },
        { "name": "Rajmata Vijayaraje Scindia Krishi Vishwavidyalaya", "city": "Gwalior" },
        { "name": "College of Agriculture, Indore", "city": "Indore" },
    ],
    "Architecture & Design": [
        { "name": "School of Planning and Architecture (SPA)", "city": "Bhopal" },
        { "name": "Maulana Azad National Institute of Technology (MANIT), Department of Architecture", "city": "Bhopal" },
        { "name": "IPS Academy, Institute of Architecture", "city": "Indore" },
    ]
};

// --- Child Components ---
const CategoryCard = ({ name, count, Icon, onClick }) => (
    <div onClick={onClick} className="flex flex-col items-center justify-center text-center p-6 bg-white rounded-xl shadow-md hover:shadow-xl hover:-translate-y-1 transition-all duration-300 cursor-pointer border-2 border-transparent hover:border-blue-500">
        <div className="text-blue-600 mb-3">
            <Icon />
        </div>
        <div className="text-gray-800">
            <h3 className="font-bold text-lg">{name}</h3>
            <p className="text-gray-500 text-sm">{count} Colleges</p>
        </div>
    </div>
);

const HomePage = ({ onCategoryClick }) => (
    <div className="w-full animate-fade-in">
        <section className="w-full max-w-5xl mx-auto text-center mb-10">
            <h1 className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-2">Explore Top Colleges</h1>
            <p className="text-base md:text-lg text-gray-600">Find the perfect college from thousands of options available across different streams.</p>
        </section>
        <div className="w-full max-w-5xl grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-4 gap-4 md:gap-6">
            {categories.map((category) => (
                <CategoryCard
                    key={category.name}
                    {...category}
                    onClick={() => onCategoryClick(category.name)}
                />
            ))}
        </div>
    </div>
);

const CollegeListPage = ({ stream, onBack }) => {
    // Scroll to top when the component mounts
    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);

    const colleges = collegeData[stream] || [];

    return (
        <section className="w-full max-w-5xl mx-auto animate-fade-in">
            <div className="mb-8">
                <button onClick={onBack} className="flex items-center text-gray-600 hover:text-blue-600 transition-colors duration-200 font-semibold">
                    <BackIcon />
                    Back to Categories
                </button>
            </div>
            <div className="bg-white rounded-xl shadow-lg p-6 md:p-8">
                <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-6">Top {stream} Colleges</h2>
                <ul className="space-y-4">
                    {colleges.map((college, index) => (
                        <li key={index} className="flex items-center justify-between p-4 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors duration-200">
                            <span className="font-semibold text-gray-700">{college.name}</span>
                            <span className="text-sm bg-blue-100 text-blue-800 font-medium px-3 py-1 rounded-full">{college.city}</span>
                        </li>
                    ))}
                </ul>
            </div>
        </section>
    );
};

// --- Main App Component ---
export default function CollegeFinder() {
    // 'home' or 'list' to control which page is shown
    const [page, setPage] = useState('home'); 
    const [selectedStream, setSelectedStream] = useState(null);

    const handleCategoryClick = (streamName) => {
        setSelectedStream(streamName);
        setPage('list'); // Switch to the list page
    };

    const handleBack = () => {
        setPage('home'); // Switch back to the home page
        setSelectedStream(null);
    };

    return (
        <div className="min-h-screen bg-gray-50 font-sans p-4 sm:p-6 md:p-8">
            <style>
                {`
                @keyframes fade-in {
                    from { opacity: 0; transform: translateY(10px); }
                    to { opacity: 1; transform: translateY(0); }
                }
                .animate-fade-in {
                    animation: fade-in 0.5s ease-out forwards;
                }
                `}
            </style>
            <main className="flex flex-col items-center">
                {page === 'home' ? (
                    <HomePage onCategoryClick={handleCategoryClick} />
                ) : (
                    <CollegeListPage stream={selectedStream} onBack={handleBack} />
                )}
            </main>
        </div>
    );
}
