        /* Hiding the text on smaller screens if the logo is dominant */
        display: none;
      }
      
      @media (min-width: 768px) {
        .header-brand span {
          display: inline; /* Show brand text on tablets and larger */
        }
      }


      /* --- Desktop Navigation --- */
      .header-nav { 
        display: none;
      }
      @media (min-width: 1024px) {
        .header-nav { 
          display: flex; 
          align-items: center;
          gap: 2rem;
        }
      }

      .nav-link { 
        color: var(--color-text-medium); 
        transition: color 0.2s ease-in-out; 
        font-weight: 600;
        padding: 0.5rem 0;
        position: relative;
        text-decoration: none;
      }
      
      .nav-link:hover { 
        color: var(--color-secondary); 
      }
      
      .nav-link::after {
        content: '';
        position: absolute;
        width: 0;
        height: 2px;
        bottom: 0;
        left: 0;
        background-color: var(--color-secondary);
        transition: width 0.3s ease-in-out;
      }
      
      .nav-link:hover::after {
        width: 100%;
      }
      
      /* --- Mobile Menu --- */
      .mobile-menu-button { 
        display: block; 
        background: none; 
        border: none; 
        color: var(--color-text-dark); 
