import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

// ✅ --- New Component for Fee PDF Uploading ---
// This component manages the state and logic for uploading a fee PDF for a single college.
const FeeUploader = ({ college, onUploadSuccess, setMessage }) => {
  const [file, setFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // Updates state when a user selects a file
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Handles the file submission to the backend
  const handleUpload = async () => {
    if (!file) {
      setMessage('Please select a PDF file first.');
      return;
    }
    setIsUploading(true);
    setMessage(''); // Clear previous messages

    const formData = new FormData();
    formData.append('feePdf', file); // 'feePdf' must match the key in the backend (upload.single('feePdf'))

    try {
      const token = localStorage.getItem('adminToken');
      const response = await fetch(`http://localhost:5000/api/colleges/${college._id}/upload-fee`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData,
      });

      if (response.ok) {
        setMessage(`✅ Fee PDF for ${college.name} updated successfully!`);
        onUploadSuccess(); // This will trigger fetchColleges in the parent component
      } else {
        const errData = await response.json();
        setMessage(`Error: ${errData.message || 'Upload failed.'}`);
      }
    } catch (err) {
      setMessage('Error: Could not connect to the server.');
    } finally {
      setIsUploading(false);
      setFile(null);
      // Visually clear the file input after upload attempt
      if (document.getElementById(`file-input-${college._id}`)) {
          document.getElementById(`file-input-${college._id}`).value = '';
      }
    }
  };

  return (
    <div className="fee-uploader" style={{ minWidth: '250px' }}>
      {/* Link to view the current PDF if it exists */}
      {college.detailedFeesUrl && (
        <a 
          href={`http://localhost:5000${college.detailedFeesUrl}`} 
          target="_blank" 
          rel="noopener noreferrer"
          className="link-view-pdf"
          style={{ display: 'block', marginBottom: '8px', color: '#007bff' }}
        >
          View Current PDF
        </a>
      )}
      <input 
        type="file" 
        id={`file-input-${college._id}`}
        accept=".pdf" 
        onChange={handleFileChange} 
        style={{ margin: '5px 0' }}
      />
      <button 
        onClick={handleUpload} 
        disabled={isUploading || !file} 
        className="btn-primary-small"
        style={{ padding: '4px 8px', fontSize: '12px' }}
      >
        {isUploading ? 'Uploading...' : 'Upload New PDF'}
      </button>
    </div>
  );
};


const AdminDashboard = ({ onLogout }) => {
  const [colleges, setColleges] = useState([]);
  const [blogs, setBlogs] = useState([]);
