    name: '', city: '', stream: 'engineering', fees: '',
    description: '', courses: '', website: ''
  });
  const [blogData, setBlogData] = useState({
    title: '', category: '', excerpt: '', content: ''
  });
  const [message, setMessage] = useState('');
