import React, { useState } from 'react';
import bgImage from '../assets/bg.png';

const SearchIcon = ({ className = "icon" }) => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" 
    fill="none" stroke="currentColor" strokeWidth="2" 
    strokeLinecap="round" strokeLinejoin="round" className={className}>
    <circle cx="11" cy="11" r="8"></circle>
    <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
  </svg>
);

const CourseChip = ({ name, onClick }) => (
  <div className="course-chip" onClick={() => onClick(name)}>
    {name}
  </div>
);

export default function HeroSection() {
  const [query, setQuery] = useState("");

  // Dummy data (college/course list)
  const colleges = ["IIM Indore", "IIT Bombay", "NIFT Delhi", "AIIMS", "SAGE University"];

  const handleSearch = () => {
    const results = colleges.filter(c =>
      c.toLowerCase().includes(query.toLowerCase())
    );
    console.log("Search results:", results);
    alert("Results: " + results.join(", "));
  };

  return (
    <div className="hero" style={{ backgroundImage: `url(${bgImage})` }}>
      <div className="hero-overlay"></div>
      <div className="hero-content">
        <h1>Find Your Dream College</h1>
        <p>A Glimpse into the Future of Your Education</p>

        <div className="search-bar-container">
          <div className="search-bar">
            <input 
              type="text" 
              placeholder="Search for colleges, courses, or exams..." 
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSearch()}
            />
            <div className="search-bar-icon">
              <SearchIcon />
            </div>
            <button 
              className="btn-primary search-bar-button" 
              onClick={handleSearch}
            >
              Search
            </button>
          </div>

          <div className="popular-searches">
            <span>Popular Searches:</span>
            <CourseChip name="MBA" onClick={setQuery} />
            <CourseChip name="B.Tech" onClick={setQuery} />
            <CourseChip name="Medical" onClick={setQuery} />
            <CourseChip name="Design" onClick={setQuery} />
          </div>
        </div>
      </div>
    </div>
  );
}
