import React from 'react';
import { Link } from 'react-router-dom';

// Import local images
import sageLogo from '../assets/SAGE_Logo.jpg';
import symbiosis from '../assets/Symbiosis.png';
import iist from '../assets/iist.png';
import acro from '../assets/Acropolis.png';
import medi from '../assets/Medicaps.png';
import prestige from '../assets/Prestige.png';
import renais from '../assets/Renaissance.png';

// --- Updated Data with 'stream' property for routing ---
const collegesData = [
    { name: "SAGE University, Indore", image: sageLogo,  streamSlug: 'engineering' },
    { name: "Symbiosis University", image: symbiosis,  streamSlug: 'management' },
    { name: "Indore Institute of Science", image: iist,  streamSlug: 'engineering' },
    { name: "Acropolis Group of Institutions", image: acro,  streamSlug: 'engineering' },
    { name: "Medicaps University", image: medi,  streamSlug: 'engineering' },
    { name: "Prestige Institute of Management And Research", image: prestige,  streamSlug: 'management' },
    { name: "Renaissance College of Commerce & Management", image: renais,  streamSlug: 'management' },
    { name: "Renaissance University", image: renais,  streamSlug: 'management' },
];

// --- Updated Sub-Component to be a clickable Link ---
const CollegeCard = ({ name, image, stream }) => (
    // The Link component wraps the card, making it clickable
    <Link to={`/colleges/${stream}/${encodeURIComponent(name)}`} className="college-card-link">
        <div className="college-card">
            <img src={image} alt={name} className="college-card-img" />
            <div className="college-card-content">
                <h4>{name}</h4>
            </div>
        </div>
    </Link>
);

// --- Main Component (Renamed to PascalCase) ---
export default function CollegesSection() {
    return (
        <section className="section">
            <div className="container">
                <div className="section-header">
                    <h2>Our Popular Colleges</h2>
                    <p>Explore top-rated institutions for your future.</p>
                </div>
                <div className="grid-4">
                    {collegesData.map((college, index) => (
                        <CollegeCard 
                            key={index} 
                            name={college.name} 
                            image={college.image}
                            stream={college.stream} 
                        />
                    ))}
                </div>
            </div>
        </section>
    );
}
