import React from 'react';

// --- CSS Styles (replaces Tailwind) ---
const AppStyles = () => (
  <style>{`
    :root {
      --color-primary: #3b82f6; /* blue-500 */
      --color-primary-dark: #2563eb; /* blue-600 */

      /* Light Theme Colors */
      --color-bg-light: #ffffff; /* white */
      --color-bg-medium: #f9fafb; /* gray-50 */
      --color-bg-dark: #f3f4f6; /* gray-100 */
      --color-text-dark: #1f2937; /* gray-800 */
      --color-text-medium: #4b5563; /* gray-600 */
      --color-text-light: #6b7281; /* gray-500 */
      --color-text-white: #ffffff;
    }

    /* --- General & Reset --- */
    body {
      margin: 0;
      font-family: sans-serif;
      background-color: var(--color-bg-medium);
      color: var(--color-text-medium);
    }
    a {
      text-decoration: none;
    }

    /* --- Utility Classes --- */
    .container {
      width: 100%;
      margin-left: auto;
      margin-right: auto;
      padding-left: 1.5rem;
      padding-right: 1.5rem;
    }
    @media (min-width: 640px) { .container { max-width: 640px; } }
    @media (min-width: 768px) { .container { max-width: 768px; } }
    @media (min-width: 1024px) { .container { max-width: 1024px; } }
    @media (min-width: 1280px) { .container { max-width: 1280px; } }

    /* --- SVG Icons --- */
    .icon {
      width: 1.5rem;
      height: 1.5rem;
    }
    .icon-large {
      width: 2rem;
      height: 2rem;
    }

    /* --- Header Section --- */
    .header {
      background-color: rgba(255, 255, 255, 0.8);
      backdrop-filter: blur(4px);
      position: sticky;
      top: 0;
      z-index: 50;
      box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.05), 0 2px 4px -1px rgba(0, 0, 0, 0.03);
      border-bottom: 1px solid var(--color-bg-dark);
    }
    .header-content {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 1rem 0;
    }
    .header-brand {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      font-size: 1.5rem;
      font-weight: bold;
      color: var(--color-text-dark);
    }
    .header-brand .icon-large { color: var(--color-primary); }
    .header-nav { display: none; }
    .header-actions { display: none; }
    .mobile-menu-button { display: block; background: none; border: none; color: var(--color-text-dark); cursor: pointer; }
    .mobile-nav {
      display: flex;
      flex-direction: column;
      gap: 1rem;
      padding: 0 1.5rem 1rem;
      background-color: var(--color-bg-light);
    }

    @media (min-width: 768px) {
      .header-nav { display: flex; gap: 1.5rem; }
      .header-actions { display: block; }
      .mobile-menu-button { display: none; }
      .mobile-nav-container { display: none; }
    }

    .nav-link {
      color: var(--color-text-medium);
      transition: color 0.3s ease-in-out;
    }
    .nav-link:hover { color: var(--color-text-dark); }
    .btn-primary {
      background-color: var(--color-primary);
      color: var(--color-text-white);
      font-weight: 600;
      padding: 0.5rem 1.5rem;
      border-radius: 0.5rem;
      border: none;
      cursor: pointer;
      transition: background-color 0.3s ease-in-out;
    }
    .btn-primary:hover { background-color: var(--color-primary-dark); }

    /* --- Hero Section --- */
    .hero {
      position: relative;
      height: 600px;
      display: flex;
      align-items: center;
      justify-content: center;
      text-align: center;
      color: var(--color-text-white);
      background-size: cover;
      background-position: center;
    }
    .hero-overlay {
      position: absolute;
      inset: 0;
      background-color: black;
      opacity: 0.5;
    }
    .hero-content {
      position: relative;
      z-index: 10;
      padding: 0 1rem;
    }
    .hero h1 {
      font-size: 2.5rem;
      font-weight: 800;
      margin-bottom: 1rem;
    }
    .hero p {
      font-size: 1.25rem;
      color: #e5e7eb; /* gray-200 for readability on dark image */
      margin-bottom: 2rem;
    }
    .search-bar-container { max-width: 42rem; margin: 0 auto; }
    .search-bar { position: relative; }
    .search-bar input {
      width: 100%;
      padding: 1rem 8rem 1rem 3rem;
      border-radius: 0.5rem;
      color: var(--color-text-dark);
      background-color: var(--color-bg-light);
      border: 1px solid #d1d5db; /* gray-300 */
      box-sizing: border-box;
    }
     .search-bar input:focus { outline: 2px solid var(--color-primary); border-color: transparent; }
    .search-bar-icon { position: absolute; left: 1rem; top: 50%; transform: translateY(-50%); color: var(--color-text-light); }
    .search-bar-button { position: absolute; right: 0.5rem; top: 50%; transform: translateY(-50%); padding: 0.625rem 1.5rem; }
    .popular-searches { margin-top: 1.5rem; display: flex; flex-wrap: wrap; justify-content: center; align-items: center; gap: 0.75rem; color: #d1d5db; }

    @media (min-width: 768px) {
      .hero h1 { font-size: 3.75rem; }
      .hero p { font-size: 1.5rem; }
    }

    /* --- Why Choose Us / Generic Section --- */
    .section {
      padding: 5rem 0;
    }
    .section-light { background-color: var(--color-bg-light); }
    .section-medium { background-color: var(--color-bg-medium); }
    .section-header { text-align: center; margin-bottom: 3rem; }
    .section-header h2 { font-size: 2.25rem; font-weight: bold; color: var(--color-text-dark); }
    .section-header p { color: var(--color-text-medium); margin-top: 0.5rem; }

    .grid-4 {
      display: grid;
      gap: 2rem;
      grid-template-columns: repeat(1, 1fr);
    }
    @media (min-width: 768px) { .grid-4 { grid-template-columns: repeat(2, 1fr); } }
    @media (min-width: 1024px) { .grid-4 { grid-template-columns: repeat(4, 1fr); } }

    /* --- Cards --- */
    .feature-card {
      background-color: var(--color-bg-light);
      padding: 1.5rem;
      border-radius: 0.5rem;
      box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.07), 0 4px 6px -2px rgba(0, 0, 0, 0.04);
      border: 1px solid var(--color-bg-dark);
      text-align: center;
      transition: transform 0.3s;
    }
    .feature-card:hover { transform: scale(1.05); }
    .feature-card-icon {
      margin: 0 auto 1rem;
      height: 4rem;
      width: 4rem;
      border-radius: 9999px;
      background-color: var(--color-primary);
      color: var(--color-text-white);
      display: flex;
      align-items: center;
      justify-content: center;
    }
    .feature-card h3 { font-size: 1.25rem; font-weight: 600; color: var(--color-text-dark); margin-bottom: 0.5rem; }
    .feature-card p { color: var(--color-text-medium); }

    .college-card {
      background-color: var(--color-bg-light);
      border-radius: 0.5rem;
      overflow: hidden;
      box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.08), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
      border: 1px solid var(--color-bg-dark);
      transition: transform 0.3s;
    }
    .college-card:hover { transform: translateY(-0.5rem); }
    .college-card img { width: 100%; height: 12rem; object-fit: cover; }
    .college-card-content { padding: 1.5rem; }
    .college-card h3 { font-size: 1.25rem; font-weight: bold; color: var(--color-text-dark); margin-bottom: 0.25rem; }
    .college-card .location { color: var(--color-primary); font-size: 0.875rem; margin-bottom: 0.75rem; }
    .college-card .description { color: var(--color-text-medium); font-size: 0.875rem; }

    .section-view-all { text-align: center; margin-top: 3rem; }
    .btn-outline {
      background-color: transparent;
      border: 2px solid var(--color-primary);
      color: var(--color-primary);
      font-weight: 600;
      padding: 0.75rem 2rem;
      border-radius: 0.5rem;
      cursor: pointer;
      transition: all 0.3s;
    }
    .btn-outline:hover { background-color: var(--color-primary); color: var(--color-text-white); }

    /* --- Course Chips --- */
    .course-chips-container {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      gap: 1rem;
    }
    .course-chip {
      background-color: var(--color-bg-dark);
      color: var(--color-text-dark);
      padding: 0.5rem 1rem;
      border-radius: 9999px;
      cursor: pointer;
      transition: background-color 0.3s, color 0.3s;
    }
    .course-chip:hover { background-color: var(--color-primary); color: var(--color-text-white); }

    /* --- Footer --- */
    .footer { background-color: var(--color-bg-dark); color: var(--color-text-medium); }
    .footer-grid {
      padding: 3rem 0;
      display: grid;
      gap: 2rem;
      grid-template-columns: repeat(1, 1fr);
    }
    @media (min-width: 768px) { .footer-grid { grid-template-columns: repeat(4, 1fr); } }

    .footer h3 {
      color: var(--color-text-dark);
      font-size: 1.125rem;
      font-weight: 600;
      margin-bottom: 1rem;
    }
    .footer p, .footer li { font-size: 0.875rem; }
    .footer ul { list-style: none; padding: 0; margin: 0; display: flex; flex-direction: column; gap: 0.5rem; }
    .footer a:hover { color: var(--color-text-dark); }
    .footer-bottom {
      margin-top: 2rem;
      padding-top: 2rem;
      border-top: 1px solid #e5e7eb; /* gray-200 */
      text-align: center;
      font-size: 0.875rem;
    }
  `}</style>
);


