import React from 'react';
import { Link } from 'react-router-dom';

// Import local images
import sageLogo from '../assets/SAGE_Logo.jpg';
import symbiosis from '../assets/Symbiosis.png';
import iist from '../assets/iist.png';
import acro from '../assets/Acropolis.png';
import medi from '../assets/Medicaps.png';
import prestige from '../assets/Prestige.png';
import renais from '../assets/Renaissance.png';

// --- Colleges Data ---
const collegesData = [
    { name: "Sage University, School of Agriculture", image: sageLogo, stream: 'agriculture' },
    { name: "Symbiosis University", image: symbiosis, stream: 'management' },
    { name: "Indore Institute of Science", image: iist, stream: 'engineering' },
    { name: "Acropolis Institute of Technology and Research", image: acro, stream: 'engineering' },
    { name: "Medicaps University", image: medi, stream: 'engineering' },
    { name: "Prestige Institute of Management And Research", image: prestige, stream: 'management' },
    { name: "Renaissance College of Commerce & Management", image: renais, stream: 'management' },
    { name: "Renaissance University", image: renais, stream: 'management' },
];

// --- College Card Component ---
const CollegeCard = ({ name, image, stream }) => {
    const trimmedName = name.trim(); // remove leading/trailing spaces

    return (
        <Link to={`/colleges/${stream}/${encodeURIComponent(trimmedName)}`} className="college-card-link">
            <div className="college-card">
                <img
                    src={image}
                    alt={trimmedName}
                    className="college-card-img"
                    loading="lazy"          // lazy-load for faster rendering
                    style={{ maxWidth: '100%', height: 'auto' }}
                />
                <div className="college-card-content">
                    <h4>{trimmedName}</h4>
                </div>
            </div>
        </Link>
    );
};

// --- Main Component ---
export default function CollegesSection() {
    return (
        <section className="section">
            <div className="container">
                <div className="section-header">
                    <h2>Our Popular Colleges</h2>
                    <p>Explore top-rated institutions for your future.</p>
                </div>

                <div className="grid-4">
                    {collegesData.map((college, index) => (
                        <CollegeCard
                            key={index}
                            name={college.name}
                            image={college.image}
                            stream={college.stream}
                        />
                    ))}
                </div>
            </div>
        </section>
    );
}
