import React from 'react';

// --- Local SVG Icons ---
// I've moved the icon into this file to resolve the import error.

const SearchIcon = ({ className = "icon" }) => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
    <circle cx="11" cy="11" r="8"></circle>
    <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
  </svg>
);


// --- Hero Section Component ---

const CourseChip = ({ name }) => (
    <div className="course-chip">
        {name}
    </div>
);

export default function HeroSection() {
    return (
        <div className="hero" style={{backgroundImage: "url('public/assets/bg.png')"}}>
            <div className="hero-overlay"></div>
            <div className="hero-content">
                <h1>Find Your Dream College</h1>
                <p>A Glimpse into the Future of Your Education</p>
                <div className="search-bar-container">
                    <div className="search-bar">
                        <input type="text" placeholder="Search for colleges, courses, or exams..." />
                        <div className="search-bar-icon">
                            <SearchIcon />
                        </div>
                         <button className="btn-primary search-bar-button">
                            Search
                        </button>
                    </div>
                    <div className="popular-searches">
                        <span>Popular Searches:</span>
                        <CourseChip name="MBA" />
                        <CourseChip name="B.Tech" />
                        <CourseChip name="Medical" />
                        <CourseChip name="Design" />
                    </div>
                </div>
            </div>
        </div>
    );
}
